ALTER TABLE image.extension ADD COLUMN "write" boolean;
UPDATE image.extension SET write=true;
ALTER TABLE image.extension ALTER COLUMN "write" SET NOT NULL;
ALTER TABLE image.extension ALTER COLUMN "write" SET DEFAULT false;

ALTER TABLE image.extension ADD COLUMN "web" boolean;
UPDATE image.extension SET web=true;
ALTER TABLE image.extension ALTER COLUMN "web" SET NOT NULL;
ALTER TABLE image.extension ALTER COLUMN "web" SET DEFAULT false;

-- remove all extensions without the used images
DELETE FROM image.extension WHERE name!='jpg' AND name!='gif' AND name!='png';
UPDATE image.extension SET description='Joint Photographic Experts Group JFIF format' WHERE name='jpg';
UPDATE image.extension SET description='CompuServe Graphics Interchange Format' WHERE name='gif';
UPDATE image.extension SET description='Portable Network Graphics' WHERE name='png';

